import Modal from '../UI/Modal';
import classes from './Cart.module.css';
import { useState } from 'react';
import productService from '../../services/product-service';

const defaultInput={
  name:'', 
  quantity:0, 
  price:'',
}

const Cart = (props) => {
  const [productName, setProductName] = useState('');
  const [productQuantity, setProductQuantity] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [errors , setErrors]=useState({nameError:'', quantityError:'', priceError:''});


  const handleAddProduct = (event) => {
    event.preventDefault();
    if (!productName) {
      setProductNameError('Product name is required');
      return;
    }

    if (!productQuantity) {
      setProductQuantityError('Amount is required');
      return;
    }

    if (!productPrice) {
      setProductPriceError('Price is required');
      return;
    }  
      productService.addProduct(productName, productQuantity, productPrice)
    .then((response) => {
      console.log('Product added successfully:', response.data);

      // setProductName('');
      // setProductQuantity('');
      // setProductPrice('');
      // props.onClose();
    })
    .catch((error) => {
      console.error('Error adding product:', error.response);
    });

  };


  return (
    <Modal onClose={props.onClose}>
      <div className={classes.total}>
        {/* <Input /> */}
        <input
          type="text"
          placeholder="Product Name"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />

        <input
          type="number"
          placeholder="Amount"
          value={productQuantity}
          onChange={(e) => setProductQuantity(e.target.value)}
        />

        <input
          type="number"
          placeholder="Price"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
      </div>
      <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>
          Close
        </button>
        <button className={classes.button} onClick={handleAddProduct } >Add</button>
      </div>
    </Modal>
  );
};

export default Cart;
