import Modal from '../UI/Modal';
import classes from './Cart.module.css';
import { useState } from 'react';
import productService from '../../services/product-service';

const defaultInput={
  name:'', 
  quantity:0, 
  price:'',
}

const Cart = (props) => {
  const [productName, setProductName] = useState('');
  const [productQuantity, setProductQuantity] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [errors , setErrors]=useState({nameError:'', quantityError:'', priceError:''});


  const handleAddProduct = (event) => {
    event.preventDefault();

      productService.addProduct(productName, productQuantity, productPrice)
    .then((response) => {
      console.log('Product added successfully:', response.data);

      setProductName('');
      setProductQuantity('');
      setProductPrice('');
      props.onClose();
    })
    .catch((error) => {
      console.error('Error adding product:', error.response.data.message);
      const message = error.response.data.message;
      message.includes("name")&& message.includes("quantity") && message.includes("price")?
      (setErrors({nameError:"required", quantityError:"required", priceError:"required"})):
       message.includes("name")

      ? (
        setErrors((prevErrors) => ({ ...prevErrors, nameError: 'Product name is required' })))
        : message.includes("email")
      ? (setEmailError(true),
        setPasswordError(false),
        setErrorMessage(message))
      : message.includes("password")
      ? (setEmailError(false),
        setPasswordError(true),
        setErrorMessage(message))
      : (setEmailError(false),
        setPasswordError(false),
        setErrorMessage(""));
    });

  };


  return (
    <Modal onClose={props.onClose}>
      <div className={classes.total}>
        {/* <Input /> */}
        <div>
        <input
          type="text"
          placeholder="Product Name"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
        {errors.nameError && <p className={classes.error}>{errors.nameError}</p>}
        </div>

      <div>
      <input
          type="number"
          placeholder="Amount"
          value={productQuantity}
          onChange={(e) => setProductQuantity(e.target.value)}
        />
        {errors.quantityError && <p className={classes.error}>{errors.quantityError}</p>}
      </div>


        <input
          type="number"
          placeholder="Price"
          value={productPrice}
          onChange={(e) => setProductPrice(e.target.value)}
        />
           {errors.priceError && <p className={classes.error}>{errors.priceError}</p>}

      </div>
      <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>
          Close
        </button>
        <button className={classes.button} onClick={handleAddProduct } >Add</button>
      </div>
    </Modal>
  );
};

export default Cart;
