import Card from '../UI/Card';
import ProductItem from './ProductItems/ProductItem';
import classes from './AvailableProducts.module.css';
import { useState , useEffect } from 'react';
import productService from '../../services/product-service';
import {AiOutlineLoading3Quarters} from "react-icons/ai";
const AvailableProducts = (props) => {
  const [products, setProducts] = useState([]);
  const [isFetching, setIsFetching] = useState(false);
  const [error, setError]=useState(false);

  useEffect(() => {
    async function fetchProducts() {
      console.log("props", props.name, props.quantity, props.price);
      setIsFetching(true);
      await productService.fetchProducts(props.name, props.quantity, props.price).then(
        (response) => {
          setProducts(response.data.Product);
          setIsFetching(false);
        },
        (error) => {
          setError( error.response.data.message)
        }
      );
    }
    fetchProducts();
  }, [props.name, props.quantity, props.price]);


  const productsList = products.map((product) => (
    <ProductItem
      id={product.id}
      key={product.id}
      name={product.name}
      quantity={product.quantity}
      price={product.price}
    />
  ));

  return (
    <section className={classes.products}>
     {!isFetching  ? (
    <Card>

      <ul>{productsList}</ul>    </Card>

      ) : (<AiOutlineLoading3Quarters className={classes.icon} />  )}

      {!isFetching && error && <Card><p>{error} </p></Card> }

</section>


  );
};

export default AvailableProducts;
